import * as vscode from 'vscode';
import { TextEditorDecorationType } from 'vscode';
import { Mode } from './mode.interface';

const motions = [
  'data:image/png;base64,',
  'data:image/gif;base64,',
  'data:image/gif;base64,',
  'data:image/gif;base64,',
];

export class WalkMode implements Mode {
  private combo = 0;

  private renderedComboCount?: number = undefined;

  private renderedRange?: vscode.Range = undefined;

  private comboDecoration?: TextEditorDecorationType;

  private comboTimerDecorationTimer?: NodeJS.Timeout;

  private comboTimerDecoration?: vscode.TextEditorDecorationType;

  private timerDurationInMilliseconds = 0;

  private timerExpirationTimestampInMilliseconds = 0;

  onDidChangeTextDocument(event: vscode.TextDocumentChangeEvent): void {
    // const timeLeft = this.timerExpirationTimestampInMilliseconds - new Date().getTime();

    // if (timeLeft <= 0) {
    //   this.comboDecoration?.dispose();
    //   return;
    // }

    const editor = vscode.window.activeTextEditor;

    if (!editor) {
      return;
    }

    const firstVisibleRange = editor.visibleRanges.find((range) => !range.isEmpty);
    this.combo += 1;

    if (!firstVisibleRange || this.combo < 1) {
      this.combo = 0;
      return;
    }

    let visibleStartLine = firstVisibleRange.start;
    let visibleEndLine = firstVisibleRange.start;
    const junbaeLocation = { top: '5%' };
    const location = vscode.workspace.getConfiguration('junbae-mode').get('location');
    if (location === 'top') {
      visibleStartLine = firstVisibleRange.start;
      visibleEndLine = firstVisibleRange.start;
    }
    if (location === 'bottom') {
      visibleStartLine = firstVisibleRange.end;
      visibleEndLine = firstVisibleRange.end;
      junbaeLocation.top = '-100px';
    }
    // const position = firstVisibleRange.start;
    // const range = new vscode.Range(position, position);
    const range = new vscode.Range(visibleStartLine, visibleEndLine);

    if (this.combo !== this.renderedComboCount || !range.isEqual(this.renderedRange!)) {
      this.renderedComboCount = this.combo;
      this.renderedRange = range;
      this.timerDurationInMilliseconds = 5 * 1000;
      this.timerExpirationTimestampInMilliseconds = new Date().getTime() + this.timerDurationInMilliseconds;
      const ranges = [range];
      this.createComboCountDecoration(this.combo, ranges, editor, junbaeLocation);
      this.createComboTimerDecoration(ranges, editor);
    }
  }

  private createComboCountDecoration = (
    count: number,
    ranges: vscode.Range[],
    editor: vscode.TextEditor,
    junbaeLocation: { top: string },
  ) => {
    const baseCss = WalkMode.objectToCssString({
      width: '50px',
      height: '50px',
      'background-size': 'contain',
      'background-repeat': 'no-repeat',
      'background-image': `url("${motions[this.combo % motions.length]}")`,
    });
    this.comboDecoration?.dispose();
    this.comboDecoration = vscode.window.createTextEditorDecorationType({
      // Note: Different decorations cannot use the same pseudoelement
      after: {
        contentText: ``,
        margin: '0.5em 0 0 0',
        color: '#FFFFFF',
        textDecoration: `none; ${WalkMode.objectToCssString({
          position: 'absolute',
          // NOTE: This positions the element off the screen when there is horizontal scroll
          // so this feature works best when "word wrap" is enabled.
          // Using "5vw" instead did not limit the position to the viewable width.
          right: '5%',
          // top:'5%',
          ...junbaeLocation,
          'font-size': '100px',
          'font-family': 'monospace',
          'font-weight': '900',
          // NOTE: Suggestion UI will still appear on top of the combo, but that is probaly a od thing
          // so the extension doesn't interfere with actual usage of the product
          'z-index': 1,
          'pointer-events': 'none',
          'text-align': 'center',
        })} ${baseCss}`,
      },
      rangeBehavior: vscode.DecorationRangeBehavior.ClosedClosed,
    });
    editor.setDecorations(this.comboDecoration, ranges);
  };

  private static objectToCssString(settings: any): string {
    let value = '';
    const cssString = Object.keys(settings)
      // eslint-disable-next-line array-callback-return, consistent-return
      .map((setting) => {
        value = settings[setting];
        if (typeof value === 'string' || typeof value === 'number') {
          return `${setting}: ${value};`;
        }
      })
      .join(' ');

    return cssString;
  }

  private createComboTimerDecoration(
    ranges: vscode.Range[],
    // @ts-expect-error
    editor: vscode.TextEditor = vscode.window.activeTextEditor,
  ) {
    if (this.comboTimerDecorationTimer) {
      clearTimeout(this.comboTimerDecorationTimer);
    }

    const updateComboTimerDecoration = () => {
      const timeLeft = this.timerExpirationTimestampInMilliseconds - new Date().getTime();

      if (timeLeft <= 0) {
        clearTimeout(this.comboTimerDecorationTimer);
        this.comboTimerDecoration?.dispose();
        return;
      }

      const timerWidth = (timeLeft / this.timerDurationInMilliseconds) * 1.5;

      const baseCss = WalkMode.objectToCssString({
        'font-size': '15px',
        'box-shadow': `0px 0px 15px #015dee`,
      });

      let guageColor = vscode.workspace.getConfiguration('junbae-mode').get('guageColor');
      const colorTheme = vscode.workspace.getConfiguration('workbench').get('colorTheme') as string;
      if (colorTheme.match('Light')) {
        guageColor = '#000000';
      }

      const createComboTimerBeforeDecoration = (lightTheme?: boolean): vscode.DecorationRenderOptions => {
        return {
          before: {
            contentText: '',
            backgroundColor: `${guageColor}`,
            width: `${timerWidth}em`,
            color: 'white',
            height: '8px',
            textDecoration: `none; ${WalkMode.objectToCssString({
              position: 'absolute',
              // NOTE: This positions the element off the screen when there is horizontal scroll
              // so this feature works best when "word wrap" is enabled.
              // Using "5vw" instead did not limit the position to the viewable width.
              right: '5%',
              top: '5%',
              'font-size': '100px',
              'font-family': 'monospace',
              'font-weight': '900',
              // NOTE: Suggestion UI will still appear on top of the combo, but that is probaly a od thing
              // so the extension doesn't interfere with actual usage of the product
              'z-index': 1,
              'pointer-events': 'none',
              'text-align': 'center',
            })} ${baseCss}`,
          },
        };
      };

      this.comboTimerDecoration?.dispose();
      this.comboTimerDecoration = vscode.window.createTextEditorDecorationType({
        // Decorations cannot use the same pseudoelement
        ...createComboTimerBeforeDecoration(),
        rangeBehavior: vscode.DecorationRangeBehavior.ClosedClosed,
        light: createComboTimerBeforeDecoration(true),
      });

      editor.setDecorations(this.comboTimerDecoration, ranges);
    };

    this.comboTimerDecorationTimer = setInterval(updateComboTimerDecoration, 50);
  }
}
