import * as vscode from 'vscode';
import { TextEditorDecorationType } from 'vscode';
import { Mode } from './mode.interface';
import { objectToCssString } from '../../utils';

const motions = [
  'data:image/png;base64,',
  'data:image/png;base64,',
  'data:image/png;base64,',
  'data:image/png;base64,',
];
export class WalkMode implements Mode {
  private combo = 0;

  private enabled = false;

  private renderedComboCount?: number = undefined;

  private renderedRange?: vscode.Range = undefined;

  private walkDecoration?: TextEditorDecorationType;

  private decorationTimer?: NodeJS.Timeout;

  private timerDecorator?: vscode.TextEditorDecorationType;

  private timerDuration = 0;

  private expiredAt = 0;

  constructor() {
    vscode.window.onDidChangeTextEditorVisibleRanges((e) => {
      this.updateDecorations(e.textEditor);
    });
  }

  onDidChangeTextDocument(event: vscode.TextDocumentChangeEvent): void {
    if (!this.enabled) {
      this.enabled = true;
    }
    const editor = vscode.window.activeTextEditor;
    if (!editor) {
      return;
    }

    this.timerDuration = 5 * 1000;
    this.expiredAt = new Date().getTime() + this.timerDuration;
    this.updateDecorations(editor);
    this.combo += 1;
  }

  private updateDecorations(editor: vscode.TextEditor) {
    if (!this.enabled) {
      return;
    }
    const firstVisibleRange = editor.visibleRanges.find((range) => !range.isEmpty);

    if (!firstVisibleRange) {
      return;
    }

    let visibleStartLine = firstVisibleRange.start;
    let visibleEndLine = firstVisibleRange.start;
    const junbaeLocation = { top: '5%', 'font-size': '50px' };
    const location = vscode.workspace.getConfiguration('junbae-mode').get('location');

    if (location === 'top') {
      visibleStartLine = firstVisibleRange.start;
      visibleEndLine = firstVisibleRange.start;
    }
    if (location === 'bottom') {
      visibleStartLine = firstVisibleRange.end;
      visibleEndLine = firstVisibleRange.end;
      junbaeLocation.top = '-100px';
      junbaeLocation['font-size'] = '100px';
    }

    const range = new vscode.Range(visibleStartLine, visibleEndLine);

    if (this.combo !== this.renderedComboCount || !range.isEqual(this.renderedRange!)) {
      this.renderedComboCount = this.combo;
      this.renderedRange = range;
      this.createWalkMotionDecorator([range], editor, junbaeLocation);
      this.createTimerDecoration([range], editor);
    }
  }

  private createWalkMotionDecorator = (
    ranges: vscode.Range[],
    editor: vscode.TextEditor,
    junbaeLocation: { top: string },
  ) => {
    const timeLeft = this.expiredAt - new Date().getTime();

    if (timeLeft <= 0) {
      this.dispose();
      return;
    }

    this.walkDecoration?.dispose();
    const decoration = vscode.window.createTextEditorDecorationType({
      after: {
        contentText: ``,
        margin: '0.5em 0 0 0',
        color: '#FFFFFF',
        textDecoration: `none; ${objectToCssString({
          width: '50px',
          height: '50px',
          'background-size': 'contain',
          'background-repeat': 'no-repeat',
          'background-image': `url("${motions[this.combo % motions.length]}")`,
          position: 'absolute',
          right: '5vw',
          ...junbaeLocation,
          'font-family': 'monospace',
          'font-weight': '900',
          'z-index': 1,
          'pointer-events': 'none',
          'text-align': 'center',
        })}`,
      },
      rangeBehavior: vscode.DecorationRangeBehavior.ClosedClosed,
    });
    this.walkDecoration = decoration;
    editor.setDecorations(this.walkDecoration, ranges);
  };

  dispose() {
    this.walkDecoration?.dispose();
    this.timerDecorator?.dispose();
    clearTimeout(this.decorationTimer);
  }

  private createTimerDecoration(ranges: vscode.Range[], editor: vscode.TextEditor) {
    if (this.decorationTimer) {
      clearTimeout(this.decorationTimer);
    }

    const updateComboTimerDecoration = () => {
      const timeLeft = this.expiredAt - new Date().getTime();

      if (timeLeft <= 0) {
        this.dispose();
        return;
      }
      const gaugeColorName = vscode.workspace.getConfiguration('junbae-mode').get('gaugeColor');
      let gaugeColor = 'white';
      if (gaugeColorName === 'red') {
        gaugeColor = '#C54B65';
      }

      const timerWidth = (timeLeft / this.timerDuration) * 1.5;

      this.timerDecorator?.dispose();
      this.timerDecorator = vscode.window.createTextEditorDecorationType({
        before: {
          contentText: '',
          backgroundColor: `${gaugeColor}`,
          width: `${timerWidth}em`,
          color: 'white',
          height: '8px',
          margin: '0.25em 0 0 0',
          textDecoration: `none; ${objectToCssString({
            'box-shadow': `0px 0px 15px #015dee`,
            position: 'absolute',
            right: '5vw',
            top: '5%',
            'font-size': '40px',
            'font-family': 'monospace',
            'font-weight': '900',
            'z-index': 1,
            'pointer-events': 'none',
            'text-align': 'center',
          })}`,
        },
        rangeBehavior: vscode.DecorationRangeBehavior.ClosedClosed,
      });

      editor.setDecorations(this.timerDecorator, ranges);
    };

    this.decorationTimer = setInterval(updateComboTimerDecoration, 50);
  }
}
